(function() {
    "use strict";

    var module = require("../xui-module.ts").default;

    module.provider("runtimeStates",
        provider,
        ["$stateProvider", "$urlRouterProvider"]);

    function provider($stateProvider, $urlRouterProvider) {
        this.$get = function() {
            return {
                addState: function(name, state) {
                    $stateProvider.state(name, state);
                },
                when: function(path, route) {
                    return $urlRouterProvider.when(path, route);
                },
                otherwise: function(name) {
                    $urlRouterProvider.otherwise(name);
                }
            };
        };
    }
})();

