(function () {
    Xui.module
        .controller("DemoTransientCacheFactoryController", DemoTransientCacheFactoryController, ["xuiTransientCacheFactory"]);

    function DemoTransientCacheFactoryController(cacheFactory) {
        var vm = this;

        vm.addToCache = function () {
            var myData = {key: "myKey", msg: "hello cache", someNumber: 99};
            var cache = cacheFactory.create("myCache");

            cache.put(myData.key, myData, 5000).then(function () {
                console.log("myData is dead!");
            });
        };

        vm.addToCache();
    }
})();