import EdgeDetectionService from "./edgeDetection-service";
import EventPropagationService from "./eventPropagation-service";
import InputProxyService from "./inputProxy-service";
import Module from "../common/module";
import NotificationService from "./notification-service";
import ScrollService from "./scrollService";
import SearchingService from "./searching-service";
import SortingService from "./sorting-service";
import SwUtil from "./swUtil-service";
import TransientCacheFactory from "./transientCacheFactory-service";
import UnitConversionService from "./unit-conversion-service";
import PerfService from "./perf-service";

export default (module:Module)=> {
    module.service("swUtil", SwUtil);
    module.service("xuiEdgeDetectionService", EdgeDetectionService);
    module.service("xuiEventPropagationService", EventPropagationService);
    module.service("xuiInputProxyService", InputProxyService);
    module.service("xuiNotificationService", NotificationService);
    module.service("xuiScrollService", ScrollService);
    module.service("xuiSearchingService", SearchingService);
    module.service("xuiSortingService", SortingService);
    module.service("xuiTransientCacheFactory", TransientCacheFactory);
    module.service("xuiUnitConversionService", UnitConversionService);
    module.service("perfService", PerfService);
};
