Xui.app().controller("ExampleColorsController", ["$http", "$scope","xuiToastService", "getJsonLocation",
    function ($http, $scope, xuiToastService, getJsonLocation) {
        var vm = this;
        vm.sampleText = "sample text";
        vm.onClipboardSuccess = onClipboardSuccess;
        vm.semanticColors = {};

        function onClipboardSuccess() {
            xuiToastService.success("Color name was copied to clipboard.");
        }

        $http.get(getJsonLocation("colors.json")).then(function(response) {
            Object.assign(vm.semanticColors, response.data);
        });
    }
]);
