"use strict";
const gulp = require("gulp");
const path = require("path");
const $ = require("@solarwinds/buildmeister/gulp-load-plugins")(gulp);

gulp.task("clean:styles-fonts", () => {
    return $.del(["src/styles/**/*", "src/fonts/**/*"], {force: true});
});

gulp.task("copy:styles", ["clean:styles-fonts"], () => {
    return gulp.src("../ng1/src/styles/**/*")
        .pipe(gulp.dest("src/styles"))
});

gulp.task("copy:fonts", ["clean:styles-fonts"], () => {
    return gulp.src("../ng1/src/fonts/**/*")
        .pipe(gulp.dest("src/fonts"))
});

gulp.task("refresh:styles-fonts", ["copy:styles", "copy:fonts"]);
