import {Component} from "@angular/core";

// TODO: Use toastService when button is clicked when it is implemented
@Component({
    selector: "xui-button-example",
    template: require<string>("./button.example.component.html")
})
export class ButtonExampleComponent {
    isDisabled = "false";
    isRepeat = "false";
    size: string = "large";
    displayStyle: string = "secondary";
    icon: string = "add";
    iconRight: string = "false";
    iconColor: string;
    isBusy: string = "false";
    isEmpty: string = "false";

    count = 100;

    handleClick() {
        console.log("Clicked!");
    }

    onCountUpClick() {
        this.count++;
        this.handleClick();
    }

    onCountDownClick() {
        this.count--;
        this.handleClick();
    }
}
