import { Component } from "@angular/core";
import { Logger } from "angular2-logger/app/core/logger";

@Component({
    selector: "checkbox-group-example",
    template: require<string>("./checkbox-group.example.component.html"),
})
export class CheckboxGroupExampleComponent {
    public vegetables = ["Cabbage", "Potato", "Tomato", "Carrot"];
    public selectedVegetables = ["Potato", "Tomato"];

    public isChecked(vegetable: string): boolean {
        return this.selectedVegetables.indexOf(vegetable) > -1;
    }

    public valuesChanged(values: any[]) {
        this.selectedVegetables = [...values];
    }

    public invertValues(event: any) {
        this.selectedVegetables = _.difference(this.vegetables, this.selectedVegetables);
    }
}
