import { Component } from "@angular/core";
import {Logger} from "angular2-logger/app/core/logger";

@Component({
    selector: "checkbox-example",
    template: require<string>("./checkbox.example.component.html"),
})
export class CheckboxExampleComponent {
    private plainValue = false;
    private commonValue = false;
    private disabledValue = true;
    private indeterminateValue = true;
    private actionValue = false;
    private validationValue = false;
    private validationMessage = "Check this and proceed.";
    private logger: Logger;
    
    constructor() {
        this.logger = new Logger();
    }
    
    public submit() {
        this.logger.warn("submit");
    }
    public action() {
        this.logger.warn("action");
        return false;
    }
    public onChange(event: any) {
        this.validationValue = event.target.checked;
    }
}
