import {Component, Inject} from "@angular/core";
import {ToastService} from "../../../../../src/services/toast.service";
import * as xui from "xui";
@Component ({
    selector: "file-upload-example",
    template: <string>require("./file-upload.example.component.html")
})
export class FileUploadExampleComponent {
    public acceptableFormats: string = "text/plain";
    public submitted: boolean;

    constructor(@Inject(xui.ToastService) private toastService: ToastService) { }

    public onFileChange(file: File) {
        this.toastService.success(`File name: ${file ? file.name : "none"}`, "File changed");
    }

    public formSubmit() {
        this.submitted = true;
    }
}
