import { Component, EventEmitter } from "@angular/core";

@Component({
    selector: "xui-message-example",
    template: require<string>("./message.example.component.html")
})
export class MessageExampleComponent {

    dismissMessage(dismissEvent: EventEmitter<undefined>) {
        console.log("dismissEvent", dismissEvent);
    }

    preventedDismiss(dismissEvent: EventEmitter<undefined>) {
        console.log("dismissEvent prevented", dismissEvent);
        dismissEvent.complete();
    }

}
