import { Component } from "@angular/core";

@Component({
    selector: "xui-page-content-example",
    template: require<string>("./page-content.example.component.html"),    
    styles: [require<string>("./page-content.example.component.less")]
})
export class PageContentExampleComponent {
    public layouts: string[] = ["fill", "fullWidth", "form"];
    public pageLayout: string = "fill";
    public isBusy: boolean = false;
    public busyMessage: string = "I'm busy now...";    
    public pageTitle: string = "Default header title";
    public linkIcon: string = "help";
    public linkText: string = "what is Firefly?";
    public linkTarget: string = "_self";
    public linkUrl: string = "http://en.wikipedia.org";
    public noPadding: string = "false";

    public makeMeBusy() {
        this.busyMessage = "I'm busy for a while...";
        this.isBusy = true;
        setTimeout(() => { this.isBusy = false; }, 2000);
    };
}
