import {Component} from "@angular/core";

// TODO: Use toastService instead of using console.log
@Component({
    template: require<string>("./panel.example.component.html")
})
export class PanelExampleComponent {
    parentHeight: number = 235;
    paneWidth: number = 250;    
    isCollapsible: string = "true";
    panelLayout: string = "fill";
    heading: string = "Header";
    headerIcon: string = "filter";
    headerIconCounter: number = 7;
    isCollapsed: string = "falsey";

    onCollapseChanged(event: boolean):void {
        this.isCollapsed = event.toString();
        console.log(event ? "Collapsed!" : "Expanded!");
    }
}
