import { Component } from "@angular/core";

@Component({
    selector: "xui-selector-example",
    template: require<string>("./selector.example.component.html")
})
export class SelectorExampleComponent {

    public selection: string;

    public indeterminate = true;

    public onSelectionChange(event: string): void {
        this.selection = event;
    }

    public makeIndeterminate(): void {
        this.indeterminate = true;
    }

}
