import { Component } from "@angular/core";
import { Logger } from "angular2-logger/app/core/logger";

@Component({
    selector: "xui-set-focus-example",
    template: require<string>("./set-focus.example.component.html")
})
export class SetFocusExampleComponent {
    public choiceState: string = "carrot";
    public carrotFocused: boolean = false;
    public onionFocused: boolean = false;
    private interval: number = 2000;

    public updateChoiceState(event: any) {
        this.choiceState = event;
    }

    public setCarrotFocus(): void {
        let _this = this;
        _this.carrotFocused = true;
        setTimeout(function () {
            _this.carrotFocused = false;
        }, this.interval);
    };

    public setOnionFocus = function () {
        let _this = this;
        _this.onionFocused = true;
        setTimeout(function () {
            _this.onionFocused = false;
        }, this.interval);
    };
}
