import {Injectable} from "@angular/core";

@Injectable()
export class MiscService {
    getJsonLocation(lookupFile: string) {
        let result = "";
        if (window.location.hostname.indexOf("apollo-docs") !== -1) {
            // will match a string which contains 5 times '/'
            let match = window.location.href.match(/^((?:[^\/]*\/){5})/);
            result = match ? match[0] : "";
        } else {
            result = window.location.origin + "/";
        }
        return result + "data/" + lookupFile;
    }
}
