import {promise, by} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class DividerAtom extends Atom {
    public static CSS_CLASS = "xui-divider";

    public isVertical(): promise.Promise<boolean> {
        return <promise.Promise<boolean>><any>super.hasClass("xui-divider--vertical");
    }

    public getCssMarginValue(): promise.Promise<string> {
        return this.isVertical().then((v)=> {
            if (v) {
                return super.getElement().getCssValue("margin");
            } else {
                return super.getElement().element(by.tagName("hr")).getCssValue("margin");
            }
        });
    }
}
