import {promise} from "@solarwinds/uzi/protractor";
import Helpers from "../helpers";
import {HelpHintAtom} from "../index.atom";
import {Atom} from "@solarwinds/uzi";

describe("USERCONTROL help-hint", () => {
    let atom: HelpHintAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("help-hint");
        atom = Atom.find(HelpHintAtom, "xui-demo-help-hint");
    });

    it("should display hint", () => {
        const text = atom.getHelpText();
        expect(text).toBe("Clicks count: 0");
    });
});
