import {promise} from "@solarwinds/uzi/protractor";
import {ImageAtom} from "../index.atom";
import Helpers from "../helpers";
import {Atom} from "@solarwinds/uzi";

describe("USERCONTROL image", () => {

    beforeEach(() => {
        Helpers.prepareBrowser("image");
    });

    it("should render images with predefined size", () => {
        const image = Atom.find(ImageAtom, "image");

        expect(image.getSize()).toEqual("300px");
    });

    it("should render images with predefined float", () => {
        const image = Atom.find(ImageAtom, "image");

        expect(image.getFloatings()).toEqual("left");
    });

    it("should render images with predefined margins", () => {
        const image = Atom.find(ImageAtom, "image");

        expect(image.hasClass("xui-image__margin-centered")).toBe(true);
    });
});
