import { promise, by, ElementFinder } from "@solarwinds/uzi/protractor";
import { Atom } from "@solarwinds/uzi";

export class PopupAtom extends Atom {

    public static CSS_CLASS = "xui-popup";

    public getPopupToggle(): ElementFinder {
        return this.getElement().element(by.css("[popupToggle]"));
    }

    public isOpened(): promise.Promise<boolean> {
        return this.getElement().element(by.css(".xui-popup__area")).isDisplayed();
    }

}
