import {promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";
import {RadioGroupAtom} from "../index.atom";
import Helpers from "../helpers";

describe("USERCONTROL Radio group", () => {

    let colorGroup: RadioGroupAtom;
    let fruitGroup: RadioGroupAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("radio-group");
        colorGroup = Atom.find(RadioGroupAtom, "test-color-radio-group");
        fruitGroup = Atom.find(RadioGroupAtom, "test-fruit-radio-group");
    });

    it("should have 'green' color checked by default", () => {
        expect(colorGroup.getValue()).toEqual("green");
    });

    it("should have 'blue' color value", async () => {
        (await colorGroup.getRadioByValue("blue")).click();
        expect(await colorGroup.getValue()).toEqual("blue");
    });

    it("should not get disabled radio button value", async () => {
        (await fruitGroup.getRadioByValue("kiwi")).click();
        expect(await fruitGroup.getValue()).not.toEqual("kiwi");
    });

});
