import {by, element, promise} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class SidebarAtom extends Atom {
    public static CSS_CLASS = "xui-sidebar";

    public isPresent = (): promise.Promise<boolean> => {
        return element(by.css(SidebarAtom.CSS_CLASS)).isPresent();
    };

    public rightSidebarIsPresent = (): promise.Promise<boolean> => {
        return element(by.css(
            ".xui-sidebar.xui-sidebar--shrink.xui-sidebar--shrink-slidein-right")).isPresent();
    };

    public leftSidebarIsPresent = (): promise.Promise<boolean> => {
        return element(by.css(".xui-sidebar.xui-sidebar-slidein-left")).isPresent();
    };
}
