import {promise, until} from "@solarwinds/uzi/protractor";
import {ISize} from "@solarwinds/uzi/webdriver";
import {Atom} from "@solarwinds/uzi";

export class SpinnerAtom extends Atom {
    public static CSS_CLASS = "xui-spinner";

    public isDisplayed() {
        return this.getElement().isDisplayed();
    }

    public getSize(): promise.Promise<ISize> {
        return this.getElement().getSize();
    }

    public waitForHidden() {
        return until.elementIsNotVisible(this.getElement().getWebElement());
    }
}
