import {by, promise, ElementFinder} from "@solarwinds/uzi/protractor";
import {Atom} from "@solarwinds/uzi";

export class SwitchAtom extends Atom {
    public static ON_CSS = "xui-switched";
    public static CSS_CLASS = "xui-switch";

    public container = (): ElementFinder => {
        return super.getElement().element(by.className("xui-switch__container"));
    };

    public caption = (): ElementFinder => {
        return super.getElement().element(by.className("xui-switch__label"));
    };

    public slider = (): ElementFinder => {
        return super.getElement().element(by.className("xui-switch__bar"));
    };

    public isOn = () => {
        return super.hasClass(SwitchAtom.ON_CSS);
    };
}
