import { promise, browser, by, element } from "@solarwinds/uzi/protractor";
import Helpers from "../../helpers";

describe("USERCONTROL drag & drop", () => {
    beforeEach(() => {
        Helpers.prepareBrowser("dragdrop");
    });

    // Chromedriver still does not support html 5 drag/drop. 29.11.2017
    xit("should be able to drop object", () => {
        const dragElement = element(by.id("xui-demo-src-object"));
        const dropElement = element(by.id("xui-demo-dest-object"));

        browser.actions().dragAndDrop(dragElement.getWebElement(), dropElement.getWebElement()).perform();
    });
});
