import { browser, by, element, ElementFinder, promise } from "@solarwinds/uzi/protractor";
import { Atom } from "@solarwinds/uzi";
import { ButtonAtom }from "../../components/index.atom";
import Helpers from "../../helpers";

describe("USERCONTROL setFocus:", () => {
    let carrotRadio: ElementFinder;
    let onionRadio: ElementFinder;
    let carrotBtnAtom: ButtonAtom;
    let onionBtnAtom: ButtonAtom;

    beforeEach(() => {
        Helpers.prepareBrowser("set-focus");
        carrotRadio = element(by.id("xui-demo-setfocus-radio-carrot")).element(by.tagName("input"));
        onionRadio = element(by.id("xui-demo-setfocus-radio-onion")).element(by.tagName("input"));
        carrotBtnAtom = Atom.find(ButtonAtom, "xui-demo-setfocus-button-carrot");
        onionBtnAtom = Atom.find(ButtonAtom, "xui-demo-setfocus-button-onion");
    });

    beforeAll(() => {
        browser.ignoreSynchronization = true;
    });

    afterAll(() => {
        browser.ignoreSynchronization = false;
    });

    it("click button that handle a prop bound to the 'xuiSetFocus' changes focus", async () => {
        await expectIsNotSelected(carrotRadio);
        await carrotBtnAtom.click();
        await expectIsSelected(carrotRadio);
    });

    it("if several component have 'xuiSetFocus' = true, focus gets the latest component", async () => {
        await expectIsNotSelected(carrotRadio);
        await expectIsNotSelected(onionRadio);
        await carrotBtnAtom.click();
        await expectIsSelected(carrotRadio);
        await expectIsNotSelected(onionRadio);
        await onionBtnAtom.click();
        await expectIsSelected(onionRadio);
        await expectIsNotSelected(carrotRadio);
    });

    async function expectIsSelected (finder: ElementFinder) {
        await expect(finder.equals(browser.driver.switchTo().activeElement())).toEqual(true);
        await browser.driver.switchTo().defaultContent();
    }

    async function expectIsNotSelected(finder: ElementFinder) {
        await expect(finder.equals(browser.driver.switchTo().activeElement())).toEqual(false);
        await browser.driver.switchTo().defaultContent();
    }
});
