import { Component, Input, Output, EventEmitter } from "@angular/core";

/**
 * <example-url>./../../../demo/index.html#/busy</example-url>
 */
@Component({
    selector: "[xui-busy]",
    template: require<string>("./busy.component.html")
})
export class BusyComponent {
    @Input() public busy: boolean;
    @Input() public showProgress: boolean;
    @Input() public message: string;
    @Input() public percent: number;
    @Input() public helpText: string;
    @Input() public cancelText: string;

    private _allowCancel: boolean;

    @Input() public set allowCancel(val: boolean) {
        this._allowCancel = val;
    }

    public get allowCancel(): boolean {
        return this._allowCancel;
    }

    @Output() public cancel = new EventEmitter();

    public onCancel() {
        this.cancel.emit();
    }
}
