import { Component, Input } from "@angular/core";
/**
 * <example-url>./../../../demo/index.html#/code-item</example-url><br />
 */

/**
 * This component is used for displaying code snippets in a listview. It supports line numbers and custom content
 * after the code snippet.
 *
 * __Name :__
 * XUI Code Item component.
 *
 * __Usage :__
 *  &lt;xui-code-item&gt;{{code}}&lt;/xui-code-item&gt;
 */
@Component({
    selector: "xui-code-item",
    template: require<string>("./code-item.component.html")
})
export class CodeItemComponent {
    /**
     * Programming language used (auto-detect if not present) - see
     * https://highlightjs.org/static/demo/ for possible values
     */
    @Input() public language: string;
    @Input() public lineNumber: number;
    @Input() public content: string;
}
