import {Component, Input} from "@angular/core";

/**
 * <example-url>./../../../demo/index.html#/content</example-url>
 */
@Component({
    selector: "xui-content",
    template: require<string>("./content.component.html")
})
export class ContentComponent {

    @Input() size: "small" | "normal" | "large" = "normal";

    public getMessageClass(): string {
        return "xui-content-" + this.size;
    }

}
