import { Component, Input, HostBinding } from "@angular/core";

import * as _ from "lodash";
/**
 * <example-url>./../../../demo/index.html#/divider</example-url>
 */

/**
 * <br>
 * Xui-divider can be used as a horizontal or vertical line divider with customizable margins.
 * If horizontal, renders &lt;hr&gt; element with 1 px heigth inside. 
 * If vertical, renders empty &lt;div&gt; with 1 px width.
 * Vertical divider should be placed into flex container.
 *
 * __Usage :__
 * &lt;xui-divider&gt; [vertical]="true" [margin]="'0px 3px'">&lt;/xui-divider&gt;
 */
 @Component({
    selector: "xui-divider",
    template: require<string>("./divider.component.html"),
    host: { "class": "xui-divider" }
})

export class DividerComponent {
    /**
     * Defines if divider is vertical. 
     */
    @Input() vertical: boolean;
    /**
     * Allows to pass custom string value for 'margin' css property. See demo-page for usage examples.
     * If not provided explicitly, relies on @margin-large-horizontal (15px) and @margin-base-vertical (5px) variables
     * from 'xui-framework-variables.less' file.
     */
    @Input() margin: string;

    @HostBinding("class.xui-divider--vertical") private get isVertical() {
        return this.vertical;
    }
    @HostBinding("style.margin") private get getVerticalMargin() {
        return (this.vertical && !_.isEmpty(this.margin)) ? this.margin : undefined;
    }

    public getHrMargin() {
        let marginStyle = !_.isEmpty(this.margin) ? { "margin": this.margin } : undefined ;
        return marginStyle;
    }
}
