import { GridComponent } from "./grid.component";

describe("components >", () => {
    describe("grid >", () => {
        let subject: GridComponent;

        beforeEach(() => {
            subject = new GridComponent();
        });

        describe("handleSelectionChange >", () => {
            it("should notify with updated selection", function () {
                spyOn(subject.selectionChange, "emit");
                const selectionMock = ["selectionMockValue"];

                subject.handleSelectionChange(selectionMock);
                expect(subject.selectionChange.emit).toHaveBeenCalledWith(selectionMock);
            });
        });
    });
});
