import { Component, Input } from "@angular/core";

export enum PaddingOptions { regular, narrow, compact, none };

/**
 * <example-url>./../../../demo/index.html#/list-item</example-url>
 */
@Component({
    selector: "xui-list-item",
    template: <string>require("./list-item.component.html")
})
export class ListItemComponent {

    @Input() public clickable = false;

    @Input() public nowrap = false;

    @Input() public paddingSize?: PaddingOptions;

    @Input() public selected = false;

    public getPaddingClass(): string {
        const paddingClass = PaddingOptions[this.paddingSize] ?
            this.paddingSize : PaddingOptions[PaddingOptions.regular];

        return `xui-list-item--${paddingClass}`;
    }

}
