import { Component, Input } from "@angular/core";

import { MenuItemComponent } from "./menu-item";

/**
 * Menu item component which is needed to perform action on click event
 */
@Component({
    selector: "xui-menu-action",
    template: `
        <a class="xui-menu-item__link" href="#" role="button" (click)="handleClick($event)">
            <xui-icon *ngIf="icon" [icon]="icon"></xui-icon>
            <ng-content></ng-content>
        </a>
    `
})
export class MenuActionComponent extends MenuItemComponent {
    /**
     * Adds icon by specified icon name
     */
    @Input() public icon: string;

    constructor() {
        super();

        this.disabled = false;
    }

    public handleClick(event: MouseEvent): void {
        event.preventDefault();

        if (this.disabled) {
            event.stopPropagation();
        }
    }

}
