import { Input, HostBinding, OnChanges, SimpleChanges } from "@angular/core";

export interface IMenuItem {
    // input json data used this interface
    checked?: boolean;
    disabled: boolean;
    title: string;
    itemType: string;
    url?: string;
    action?: void;
}

/**
 * Base class for menu items. Adds styles to host element
 */
export abstract class MenuItemComponent implements OnChanges {
    /**
     * Disables action, link and option components
     */
    @Input() public disabled?: boolean;

    @HostBinding("class.xui-menu-item") private setDefaultClass = true;

    @HostBinding("class.xui-menu-item--disabled") private setDisabledClass: boolean;

    public ngOnChanges(changes: SimpleChanges): void {
        const disabled = changes["disabled"] && changes["disabled"].currentValue || false;

        this.setDisabledClass = disabled;
    }

}
