import { Component, Input } from "@angular/core";

import { MenuItemComponent } from "./menu-item";

/**
 * Represents menu link item of xui-menu component with additional bindings and style
 */
@Component({
    selector: "xui-menu-link",
    template: `
        <a class="xui-menu-item__link" role="link"
            [href]="url"
            [target]="target"
            (click)="handleClick($event)">

            <xui-icon *ngIf="icon" [icon]="icon"></xui-icon>
            <ng-content></ng-content>
        </a>
    `
})
export class MenuLinkComponent extends MenuItemComponent {
    /**
     * Sets inner "href" attribute of anchor tag
     */
    @Input() public url: string;
    /**
     * Sets inner "target" attribute of anchor tag
     */
    @Input() public target = "";
    /**
     * Adds icon by specified icon name
     */
    @Input() public icon: string;

    constructor() {
        super();

        this.disabled = false;
    }

    public handleClick(event: MouseEvent): void {
        if (this.disabled) {
            event.preventDefault();
            event.stopPropagation();
        }
    }

}
