import {
    Directive,
    HostListener,
    Output,
    EventEmitter,
    ElementRef
} from "@angular/core";

@Directive({
    selector: "[popupToggle]"
})
export class PopupToggleDirective {
    constructor(public host: ElementRef) {}

    @HostListener("click")
    public handleCLick(): void {
        this.toggle.emit();
    }

    @Output()
    public toggle = new EventEmitter();
}
