import {SwitchComponent} from "./switch.component";

describe("components >", () => {
    describe("switch >", () => {
        let subject: SwitchComponent;

        beforeEach(() => {
            subject = new SwitchComponent();
        });

        describe("toggle >", () => {
            it("should turn off turned on switch", () => {
                subject.turned = true;
                subject.toggle();

                expect(subject.turned).toBeFalsy();
            });

            it("should turn on turned off switch", () => {
                subject.turned = false;
                subject.toggle();

                expect(subject.turned).toBeTruthy();
            });
        });
    });
});
