import {Component, OnInit, Input, Output, EventEmitter} from "@angular/core";

/**
 * <example-url>./../../../demo/index.html#/switch</example-url>
 */
@Component({
    selector: "xui-switch",
    template: require<string>("./switch.component.html")
})

export class SwitchComponent implements OnInit {
    @Input() public turned: boolean;
    @Input() public inactive: boolean;

    @Output() turnedChange: EventEmitter<boolean> = new EventEmitter<boolean>();
    toggle() {
        this.turned = !this.turned;
        this.turnedChange.emit(this.turned);
    }

    ngOnInit(): void {
        this.turned = !!this.turned;
    }
}
