import { InjectionToken } from "@angular/core";

export interface IImagesPresetItem {
    svgFile: string;
    name: string;
    brushType: string;
    code: string;
}

export type ImageFloat = "left" | "right";

export type ImageMargin = "centered" | "small" | "large";

const imagesData = require<Array<IImagesPresetItem>>("../../../data/images.json");

if (!imagesData) {
    throw Error("No images.json found!");
}

export const ImagesPresetToken = new InjectionToken<Array<IImagesPresetItem>>("IMAGES_PRESET");

export const IMAGES_PRESET = imagesData;
