import {Injectable} from "@angular/core";

export interface IconData {
    svgFile: string;
    name: string;
    cat_namespace: string;
    category: string;
    brushType: string;
    cssAmendments: string;
    code: string;
}

@Injectable()
export class IconService {
    static ICONS = require<IconData[]>("../../../data/icons.json");

    getIconData(iconName: string): IconData {
        return IconService.ICONS.find((iconData: IconData) => iconData.name === iconName);
    };
}
